<section class="profile"><h1><?php echo htmlspecialchars( $data['name'], ENT_QUOTES ); ?></h1><p>Date of birth: <?php echo htmlspecialchars( $data['dob'], ENT_QUOTES ); ?></p><?php if ( $data['showColors'] ) { ?><h2>Favorite Colors</h2><div><?php foreach ( $data['favoriteColors'] as $data_1 ) { ?><div><h3><?php echo htmlspecialchars( $data_1['label'], ENT_QUOTES ); ?></h3><div><h4>Connotations</h4><ul><?php foreach ( $data_1['connotations'] as $data_2 ) { ?><li><?php echo htmlspecialchars( $data_2, ENT_QUOTES ); ?></li><?php } ?></ul></div></div><?php } ?></div><?php } ?><?php if ( $data['showArtists'] ) { ?><h2>Favorite Artists</h2><div><?php foreach ( $data['favoriteArtists'] as $data_1 ) { ?><div><?php echo htmlspecialchars( $data_1['name'], ENT_QUOTES ); ?> | <?php echo htmlspecialchars( $data_1['genre'], ENT_QUOTES ); ?></div><?php } ?></div><?php } ?><h2>Character traits</h2><?php foreach ( $data['traits'] as $data_1 ) { ?><div>A trait: <?php echo htmlspecialchars( $data_1, ENT_QUOTES ); ?></div><?php } ?><h2>Character traits raw</h2><?php foreach ( $data['traits'] as $data_1 ) { ?><?php echo htmlspecialchars( $data_1, ENT_QUOTES ); ?><?php } ?><h2>Combining control + replace variables</h2><?php if ( $data['showTest'] === 'yes' ) { ?><?php echo htmlspecialchars( $data['name'], ENT_QUOTES ); ?><?php } ?><h2>Combining control + list variables</h2><?php if ( $data['showTest'] === 'yes' ) { ?><?php foreach ( $data['traits'] as $data_1 ) { ?><?php echo htmlspecialchars( $data_1, ENT_QUOTES ); ?><?php } ?><?php } ?></section>


